import React from 'react'; // Import the Component component from React

import ProjectHeader from './../projectPage/ProjectHeader.js'
import HeroImage from './../projectPage/HeroImage.js'
import SectionOne from './../projectPage/SectionOne.js'
import Image from './../projectPage/Image.js'
import Text from './../projectPage/Text.js'


import Divider from './../projectPage/Divider.js';


const data = {
  'heroImage':require('./../../content/projects/pause/hero-image.png'),
}

const a = {
  'title':'EAST Framework for Behavioural change',
  'text':"Pause is an app, targeted at adolescents, that aims to address excessive use of social networking sites by empowering the user to control their screen time. The longer the user scrolls, the longer content takes to load and the harder scrolling becomes, breaking the flow. This break is leveraged as a positive nudge to stop scrolling. Scrolling and loading speed will slowly recover as a reward when social media isn't being used.",
  'image':require('./../../content/projects/pause/1.png'),
}



function Pause(props) {
    return(
      <div>
        <ProjectHeader
         projectKey='pause'
        />
        <HeroImage
         heroImage={data.heroImage}
        />
        <Divider/>
        <Text
         text='Concept'
         col="col-12"
         type="title"
        />
        <SectionOne
         title={a.title}
         text={a.text}
         image={a.image}
        />
        <Divider/>
        <Text
         text='App Mockup'
         col="col-12"
         type="title"
        />
      </div>
    )
}

export default Pause;
