import React from 'react'; // Import the Component component from React
import Markdown from 'react-markdown'

class Image extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      image:props.image,
      col:props.col,
      height:props.height
    }
  }

  render() {
    return(
      <div className={"image-wrapper "+this.state.col}>
        <img src={this.state.image} style={{height:this.state.height}}/>
      </div>
    )
  }
}

export default Image;
