import React, { useEffect, useRef } from 'react'; // Import the Component component from React
import Carousel from 'nuka-carousel';
import SectionOne from './../projectPage/SectionOne.js'

const data = {
  'heroImage':require('./../../content/projects/panaseer/hero-image.png'),
}

const a = {
  'image':require('./../../content/projects/panaseer/1.png')
}

const b = {
  'title':'IR Spectroscopy',
  'text':'The core technology behind Panaseer is Infrared spectroscopy which allows for the identification of organic compunds by projecting a range of infrared frequencies onto the compund and measuring which frequencies are returned, as different types of chemical bond absorb different frequencies. Taking the types and numbers of bonds into account, specific compounds can be identified by comparison to a database.',
  'image':require('./../../content/projects/panaseer/23.jpg')
}

const c = {
  'height':"auto",
  'col':'col-3',
  'textSpacing':'10px',
  'image1':require('./../../content/projects/panaseer/5.png'),
  'image2':require('./../../content/projects/panaseer/4.png'),
  'image3':require('./../../content/projects/panaseer/6.JPG'),
}

const d = {
  'height':"auto",
  'col':'col-3',
  'textSpacing':'10px',
  'image1':require('./../../content/projects/panaseer/7.png'),
  'image2':require('./../../content/projects/panaseer/8.png'),
  'image3':require('./../../content/projects/panaseer/9.png'),
  'image4':require('./../../content/projects/panaseer/10.png'),
  'image5':require('./../../content/projects/panaseer/11.png'),
  'image6':require('./../../content/projects/panaseer/12.png'),
  'image7':require('./../../content/projects/panaseer/13.png'),
  'image8':require('./../../content/projects/panaseer/14.png'),
  'image9':require('./../../content/projects/panaseer/15.png'),
}

const e = {
  'image1':require('./../../content/projects/panaseer/16.png'),
  'image2':require('./../../content/projects/panaseer/17.png'),
  'image3':require('./../../content/projects/panaseer/18.png'),
  'image4':require('./../../content/projects/panaseer/19.png'),
  'col':'col-3',
  'height':'auto',
}



function Panaseer(props) {
  const carouselRef = useRef(null)
  useEffect(() => {
    carouselRef.current.setDimensions()

    }, [])

    return(
      <Carousel
        enableKeyboardControls={true}
        disableEdgeSwiping={true}
        heightMode="max"
        speed={500}
        withoutControls={true}
        width="100%"
        ref={carouselRef}
        >
      	<SectionOne
          title={b.title}
          image={b.image}
          text={b.text}
        />
        <SectionOne
          title={c.title}
          image={c.image1}
          text={b.text}
        />
      </Carousel>
    )
}

export default Panaseer;
