import React from 'react'; // Import the Component component from React
import Markdown from 'react-markdown'

class SectionOne extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      title:props.title,
      text:props.text,
      image:props.image
    }
  }

  render() {
    return(
      <div className="container-fluid">
          <div className='sectionOne-title'>{this.state.title}</div>
          <div className="col-lg-6 sectionOne-img set-bg" style={{backgroundImage: "url(" + this.state.heroImage + ")"}}></div>
          <div className="col-lg-6">{this.state.text}</div>
      </div>
    )
  }
}

export default SectionOne;
