import React from 'react'; // Import the Component component from React
import ReactPlayer from 'react-player'
import ScrollAnimation from 'react-animate-on-scroll';

class PhoneVideo extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      title:props.title,
      text:props.text,
      video:props.video
    }
  }

  render() {
    return(
      <ScrollAnimation animateIn="fadeIn">
        <div className="container-fluid p-md-0 ">
          <div className="row PhoneVideo">
            <div className='col-lg-8'>
              <h1 className="sectionOne-title">{this.state.title}</h1>
              <p className="sectionOne-text">{this.state.text}</p>
            </div>
            <div className='col-lg-4'>
              <div className='player-wrapper'>
                <ReactPlayer
                 className='react-player'
                 url={this.state.video}
                 controls
                 volume={10}
                 height='600px'
                 width='100%'
                />
              </div>
            </div>

          </div>
        </div>
      </ScrollAnimation>
    )
  }
}

export default PhoneVideo;
