import React, { useEffect, useRef, useState } from 'react'; // Import the Component component from React
import Carousel from 'nuka-carousel';

function ProjectCarousel(props) {
  // Fixes Carousel not loading initially
  const carouselRef = useRef(null)
    useEffect(() => {
      setTimeout(() => {carouselRef.current.onResize();}, 0);
    }, [])

  // Changes slide info based on slide
  const [currentSlide, changeSlide] = useState(0);
  const [infoOpacity, changeOpacity] = useState(1);
  console.log(infoOpacity)

  return(
    <div>
      <div className="carouselWrapper">
        <Carousel
          afterSlide={currentIndex => {changeSlide(currentIndex);  changeOpacity(1);}}
          enableKeyboardControls={true}
          disableEdgeSwiping={true}
          heightMode="max"
          speed={500}
          width="100%"
          vertical={false}
          ref={carouselRef}
          >
          {props.slides.map((slide) => (
            <div className="slide">
              <img src={slide} alt="default"/>
            </div>
          ))}
        </Carousel>
      </div>

      <div className='slideInfo' style={{opacity:infoOpacity}}>
          <hr className="solid"/>
        {props.slideInfo[currentSlide]}
      </div>
    </div>
  )
}

export default ProjectCarousel;
