import React from 'react';
import Thumbnail from './Thumbnail.js'; // Import the Thumbnail component
import '../css/App.css';

import {CSSTransition,TransitionGroup} from 'react-transition-group';


class Projects extends React.Component {
  constructor(props) {
    super(props);

    const balancingRobot = {
      'key':'balancingRobot',
      'link':"/balancingRobot",
      'image':require('./../content/projects/balancingRobot/thumbnail.png'),
      'title':"Strip The Willow: Dancing Robot",
      'question':"How can **Signal Processing** be used to create a **Dancing Robot**?",
      'col':"col-md-4",
      'category':['cod']
    }

    const beatbox = {
      'key':'beatbox',
      'link':"/beatbox",
      'image':require('./../content/projects/beatbox/thumbnail.jpg'),
      'title':"Beatbox",
      'question':"How can **Heart Rate** be visualised using **Mechatronics**?",
      'col':"col-md-4",
      'category':['cod']
    }

    const bigData = {
      'key':'bigData',
      'link':"/bigData",
      'image':require('./../content/projects/bigData/thumbnail.jpg'),
      'title':"Big Data",
      'question':"How can **Data Analytics** be used to **Explain and Predict** knife crime in London ?",
      'col':"col-md-4",
      'category':['cod']
    }

    const embla = {
      'key':'embla',
      'link':"/embla",
      'image':require('./../content/projects/embla/thumbnail.png'),
      'title':"Embla",
      'question':"How can **Workplace Environments** be designed to **Reduce Stress**?",
      'col':"col-md-8",
      'category':['str','des']
    }

    const jumpingRobot = {
      'key':'jumpingRobot',
      'link':"/jumpingRobot",
      'image':require('./../content/projects/jumpingRobot/thumbnail.png'),
      'title':"LeapFrog",
      'question':"How can **Mechanics, Finite Element Analysis and 3D Modelling** be used to design a **Jumping Robot**?",
      'col':"col-md-4",
      'category':['eng']
    }

    const pacman = {
      'key':'pacman',
      'link':"/pacman",
      'image':require('./../content/projects/pacman/thumbnail.png'),
      'title':"Pacman",
      'question':"How can **Algorithm Design** be used to **Optimise** Pacman?",
      'col':"col-md-4",
      'category':['cod']
    }

    const pause = {
      'key':'pause',
      'link':"/pause",
      'image':require('./../content/projects/pause/thumbnail.png'),
      'title':"Pause",
      'question':"How can **Behavioural Psychology** be used to address **Social Media Addiction**?",
      'col':"col-md-4",
      'category':['des']
    }

    const peoplePod = {
      'key':'peoplePod',
      'link':"/peoplePod",
      'image':require('./../content/projects/peoplePod/thumbnail.jpeg'),
      'title':"ModPod",
      'question':"How can **Autnomous Vehichles** be used to tackle the **Last Mile Problem**?",
      'col':"col-md-4",
      'category':['str','des']
    }

    const revEng = {
      'key':'revEng',
      'link':"/revEng",
      'image':require('./../content/projects/revEng/thumbnail.jpg'),
      'title':"RevEng",
      'question':"How can **Thermodynamics and Material Analysis** be used to make the **Products More Sustainable**?",
      'col':"col-md-8",
      'category':['str','eng']
    }

    const spiro = {
      'key':'spiro',
      'link':"/spiro",
      'image':require('./../content/projects/spiro/thumbnail.png'),
      'title':"Spiro",
      'question':"How can a cost-effective way to **Measure Lung Capacity** be used to help **diagnose COPD globally**?",
      'col':"col-md-4",
      'category':['cod','str']
    }

    this.state = {
      activeFilter:[true,false,false,false,false],
      projects: [
        balancingRobot,beatbox,bigData,embla,jumpingRobot,pacman,pause,peoplePod,revEng,spiro
      ],
    }

    this.state.filterProjects=this.state.projects

    this.filter = this.filter.bind(this)
  }

  filter(i, category){
    var activeFilter = [false,false,false,false,false];
    activeFilter[i] = true;
    this.setState({
      filterIndex:i,
      activeFilter:activeFilter,
      filterProjects:(category==='all') ? this.state.projects : this.state.projects.filter(project => project.category.includes(category))
    });
  }

  render() {
    const activeFilter = this.state.activeFilter
    return (
        <section className="portfolio-section">
      		<div className="container">
      			<ul className="portfolio-filter">
      				<li className={activeFilter[0]? "filter-active" : "filter-inactive"} onClick={() => this.filter(0, 'all')}>All</li>
      				<li className={activeFilter[1]? "filter-active" : "filter-inactive"} onClick={() => this.filter(1, 'cod')}>Coding and Electronics</li>
      				<li className={activeFilter[2]? "filter-active" : "filter-inactive"} onClick={() => this.filter(2, 'des')}>Design</li>
      				<li className={activeFilter[3]? "filter-active" : "filter-inactive"} onClick={() => this.filter(3, 'eng')}>Engineering Analysis</li>
      				<li className={activeFilter[4]? "filter-active" : "filter-inactive"} onClick={() => this.filter(4, 'str')}>Strategy and Systems</li>
      			</ul>
      		</div>
          <TransitionGroup>
            <CSSTransition
                    classNames="portfolioFade"
                    timeout={800}
                    key={this.state.filterIndex}
                  >
          		<div className="container-fluid p-md-0 ">
          			<div className="row portfolio-area">
                  {this.state.filterProjects.map((thumbnail) => (
                    <Thumbnail
                      key={thumbnail.title}
                      link={thumbnail.link}
                      image={thumbnail.image}
                      title={thumbnail.title}
                      question={thumbnail.question}
                      col={thumbnail.col}
                      />
                  ))}
          			</div>
          		</div>
            </CSSTransition>
          </TransitionGroup>
      	</section>

    )
  }
}

export default Projects;
