import React from 'react'; // Import the Component component from React

import ProjectHeader from './../projectPage/ProjectHeader.js'
import HeroVideo from './../projectPage/HeroVideo.js'
import SectionOne from './../projectPage/SectionOne.js'
import Image from './../projectPage/Image.js'
import Text from './../projectPage/Text.js'

import VideoSection from './../projectPage/VideoSection.js'

import Divider from './../projectPage/Divider.js';


const data = {
  'heroImage':require('./../../content/projects/pacman/hero-image.jpg'),
}

const a = {
  'title':'Pacman',
  'text':'The "Point Potential" function uses a breadth first search to "see" the entire map. The function gives a value to each position on the map: Pacman is then directed towards the position with the highest point potential. This potential increases the further away it is from the ghosts, if there is food on the position and if there is a power up. Pacman prioritises staying away from ghosts and heading towards power-ups. Lastly, it considers food.',
  'image':require('./../../content/projects/pacman/1.png'),
}




function Pacman(props) {
    return(
      <div>
        <ProjectHeader
         projectKey='beatbox'
        />
        <HeroVideo
         heroVide={data.heroVideo}
        />
        <Divider/>
        <Text
         text='**Sample Code**'
         col="col-12"
         height="10px"
         align="left"
         font="100px"
        />
        <SectionOne
         title={a.title}
         text={a.text}
         image={a.image}
        />
      </div>
    )
}

export default Pacman;
