import React, { useEffect, useRef } from 'react'; // Import the Component component from React
import Carousel from 'nuka-carousel';
import '../css/App.css';
import HomepageProject from './homepageProject.js'

const project_total = "03"

function Home(props) {
  // Fixes Carousel not loading initially
  const carouselRef = useRef(null)
    useEffect(() => {
      setTimeout(() => {carouselRef.current.onResize();}, 0);
    }, [])

  return (
    <Carousel
      enableKeyboardControls={true}
      disableEdgeSwiping={true}
      heightMode="max"
      speed={500}
      width="100%"
      vertical={false}
      ref={carouselRef}
      defaultControlsConfig={{
        nextButtonTextClassName: 'carouselNext',
        prevButtonText: 'carouselNextPrev',
        pagingDotsStyle: {
          fill: "#4A03FF"
        }
      }}
      >
      <HomepageProject
       projectKey={'panaseer'}
       key="01"
       number="01"
       total={project_total}
       colour="#0FFFB0"
      />
      <HomepageProject
       projectKey={'joyStory'}
       key="02"
       number="02"
       total={project_total}
       colour="#FF491C"
      />
      <HomepageProject
       projectKey={'spiro'}
       key="03"
       number="03"
       total={project_total}
       colour="#FFEE1C"
      />
    </Carousel>
  );
}

export default Home;
