import React from 'react'; // Import the Component component from React

import ProjectHeader from './../projectPage/ProjectHeader.js'
import HeroImage from './../projectPage/HeroImage.js'
import Image from './../projectPage/Image.js'
import SectionOne from './../projectPage/SectionOne.js'
import VideoSection from './../projectPage/VideoSection.js'

import Divider from './../projectPage/Divider.js';


const data = {
  'heroImage':require('./../../content/projects/spiro/hero-image.png'),
}

const a = {
  'title':'Background',
  'text':'A device that cheaply and efficiently measures tidal lung volume (volume of unforced breathing. Subsaharan countries don’t have access to spirometers so most of the data regarding Chronic Obstructive Pulmonary Disease (COPD) is neither accurate nor real. More than 90% of COPD deaths occur in low­ and middle­ income countries, where effective strategies for prevention and control are not always implemented or accessible. The idea is to raise awareness of these lung related issues within local populations. Using quiet breathing only : full inspiration and expiration can be used but no forced manoeuvres.',
  'image':require('./../../content/projects/spiro/1.png'),
}

const b = {
  'title':'Turbine Design',
  'text':'Solidworks Flow Analysis was used to test different turbine designs, varying in blade thickness and number of blades. The pressure loss in the pipe was modelled, being a source of error in the calculated volumes. Turbine designs were 3D-printed and tested physically inside the housing to validate the design choice. A 4-blade, 4mm turbine performed the best in digital and physical prototyping.',
  'image':require('./../../content/projects/spiro/3.png'),
}

const c = {
  'title':'Electronics and Coding',
  'text':'The "works-like" prototype was built to demonstrate the measurement of tidal lung volume. A single turbine measures the breath rate using hall-effect sensors. The breath rate can then be integrated with respect to time to give a volume reading. In this way, the patient can breath several times and the readings can be averaged into an estimate of the exhale volume which can then be doubled to give an estimate of the tidal volume, as exhale and inhale volume are practically identical. This avoided the need for a second turbine to measure inhale volume',
  'image':require('./../../content/projects/spiro/4.png'),
}


function Spiro(props) {
    return(
      <div>
        <ProjectHeader
         projectKey='spiro'
        />
        <HeroImage
         heroImage={data.heroImage}
        />
        <Divider/>
        <SectionOne
          text={a.text}
          title={a.title}
          image={a.image}
        />
        <Divider/>
        <SectionOne
          text={b.text}
          title={b.title}
          image={b.image}
        />
        <Divider/>
        <SectionOne
          text={c.text}
          title={c.title}
          image={c.image}
        />
      </div>
    )
}

export default Spiro;
