import React from 'react'; // Import the Component component from React

import ProjectHeader from './../projectPage/ProjectHeader.js'
import HeroImage from './../projectPage/HeroImage.js'
import Image from './../projectPage/Image.js'
import VideoSection from './../projectPage/VideoSection.js'

import Divider from './../projectPage/Divider.js';


const data = {
  'heroImage':require('./../../content/projects/spiro/hero-image.png'),
}

const a = {
  'title':'Background',
  'text':'A device that cheaply and efficiently measures tidal lung volume (volume of unforced breathing. Subsaharan countries don’t have access to spirometers so most of the data regarding Chronic Obstructive Pulmonary Disease (COPD) is neither accurate nor real. More than 90% of COPD deaths occur in low­ and middle­ income countries, where effective strategies for prevention and control are not always implemented or accessible. The idea is to raise awareness of these lung related issues within local populations. Using quiet breathing only : full inspiration and expiration can be used but no forced manoeuvres.',
  'image':require('./../../content/projects/spiro/1.png'),
}


function Spiro(props) {
    return(
      <div>
        <ProjectHeader
         projectKey='spiro'
        />
        <HeroImage
         heroImage={data.heroImage}
        />
        <Divider/>
        <SectionOne
          text={a.text}
          title={a.title}
          image={a.image}
        />

      </div>
    )
}

export default Spiro;
