import React, { useState } from 'react';
import Thumbnail from './Thumbnail.js'; // Import the Thumbnail component
import '../css/App.css';

class Projects extends React.Component {
  constructor(props) {
    super(props);

    const embla = <Thumbnail
      link="/embla"
      image={require('./../content/projects/embla/thumbnail.png')}
      title="Embla"
      col="col-md-8"/>


    const beatbox = <Thumbnail
      link="/beatbox"
      image={require('./../content/projects/beatbox/thumbnail.jpg')}
      title="Beatbox"
      col="col-md-4"/>

    this.state = {
      activeFilter:[true,false,false,false,false],
      projects: [
        embla,
        beatbox
      ]
    }

    this.filterAll = this.filterAll.bind(this)
    this.filterCod = this.filterCod.bind(this)
    this.filterEle = this.filterEle.bind(this)
    this.filterMec = this.filterMec.bind(this)
    this.filterStr = this.filterStr.bind(this)

  }

  filterAll(){
    this.setState({
      activeFilter:[true,false,false,false,false],
    });
  }

  filterCod(){
    this.setState({
      activeFilter:[false,true,false,false,false],
    });
  }

  filterEle(){
    this.setState({
      activeFilter:[false,false,true,false,false],
    });
  }

  filterMec(){
    this.setState({
      activeFilter:[false,false,false,true,false],
    });
  }

  filterStr(){
    this.setState({
      activeFilter:[false,false,false,false,true],
    });
  }


  render() {
    const activeFilter = this.state.activeFilter
    return (
        <section className="portfolio-section">
      		<div className="container">
      			<ul className="portfolio-filter">
      				<li className={activeFilter[0]? "filter-active" : "filter-inactive"} onClick={this.filterAll}>All</li>
      				<li className={activeFilter[1]? "filter-active" : "filter-inactive"} onClick={this.filterCod}>Coding</li>
      				<li className={activeFilter[2]? "filter-active" : "filter-inactive"} onClick={this.filterEle}>Electronics</li>
      				<li className={activeFilter[3]? "filter-active" : "filter-inactive"} onClick={this.filterMec}>Mechanical Engineering</li>
      				<li className={activeFilter[4]? "filter-active" : "filter-inactive"} onClick={this.filterStr}>Strategy and Systems</li>
      			</ul>
      		</div>
      		<div className="container-fluid p-md-0 ">
      			<div className="row portfolio-area">
              {this.props.defaultColumns.map((column) => (
                <Thumbnail
                value={column}
                onHeaderClick={this.handleSort}
                />
                ))}
      			</div>
      		</div>
      	</section>

    )
  }
}

export default Projects;
