import React, { useState, useEffect, useRef } from 'react';

function SlideInfo(props) {

  return(
    <div>
      <i
        className={`${isExpanded ? "fa fa-times" : "fa fa-bars"}`}
        aria-hidden="true"
        onHover={() => {toggleExpanded(!isExpanded);}}
      />
      <div className={`slideInfo  + ${props.infoShow ? null:"collapsed-down"}`}>
        <h1>{props.info[0]}</h1>
        <h2>{props.info[1]}</h2>
      </div>
    </div>
  )
}

export default SlideInfo;
