import React, { useState } from 'react'; // Import the Component component from React
import Typing from 'react-typing-animation';


function Intro(props) {

  const [introVis, changeIntroVis] = useState(true)

  return (
    <div className="intro-wrapper">
      <div
        className={`${introVis ? "introVis":"introInvis"}`}
      >
        <Typing onFinishedTyping={() =>{}}>
          <a>Hi, I'm William, a design engineer based in London.</a>
        </Typing>
      </div>
    </div>
  );
}

export default Intro;
