import React from 'react'; // Import the Component component from React
import ProjectCarousel from './projectPage/projectCarousel.js'


function About(props) {
  const slides = [
    {
      id:3,
      src:require('./../content/about/portrait_bw.jpeg')
    },
  ]

  const slideInfo = [
    {
      text:
        [
          "High Ex(perience)",
          "Currently: Recent Imperial College Graduate | Product, industrial, graphic: design is all about experience. In an increasingly high tech world, my approach as a design engineer is to create meaningful, novel and functional experiences by starting with the human condition.",
        ],
      width:'30vw',
      top:'10vh',
    },
  ]

  return(
    <ProjectCarousel
      about={true}
      slides={slides}
      slideInfo={slideInfo}
    />
  )
}

export default About;
