import React from 'react'; // Import the Component component from React

import ProjectHeader from './../projectPage/ProjectHeader.js'
import HeroImage from './../projectPage/HeroImage.js'
import SectionOne from './../projectPage/SectionOne.js'
import Image from './../projectPage/Image.js'


const data = {
  'heroImage':require('./../../content/projects/bigData/hero-image.png'),
}

const a = {
  'title':'Explaining',
  'text':'Linear regression was used to understand which factors correlate with knife crime. We compared knife crime data to 32 different variables ranging from unemployment to childhood obesity. The data all came from ONS statistics, separated by borough. The R-squared value, tells us how correlated our variables are. The maximum R-squared value is 1 and the minimum is 0 and a value of greater than 0.64 is considered a strong correlation. The data suggests that boroughs with a higher prevalence of unemployment have a higher prevalence of knife crime and that boroughs with higher amounts of income support have lower levels of knife crime, suggesting that socio-economic factors play a role in the prevalence of knife crime. This model has an r-squared of 0.809',
  'image':require('./../../content/projects/bigData/1.png'),
}

const b = {
  'title':'Predicting',
  'text':'To build the predictive model, 11 variables were considered and their impact on the stop and search outcomes was determined. These included time of year, gender, ethnicity age and location. In the end, location was by far the best predictor of stop and search outcome, with age, ethnicity and day of the week playing very minor roles in improving the accuracy. The confusion matrix shows (from left to right and top to bottom) the number of true positives, false negatives, false positives and true negatives. In this context, too false negatives may result in ineffective policing whereas too many false positives may result in innocent individuals feeling unnecessarily stopped.',
  'image':require('./../../content/projects/bigData/2.png'),
}



function BigData(props) {
    return(
      <div>
        <ProjectHeader
         projectKey='bigData'
        />
        <div className="line-separator"></div>
        <HeroImage
         heroImage={data.heroImage}
        />
        <SectionOne
         title={a.title}
         text={a.text}
         image={a.image}
        />
        <SectionOne
         title={b.title}
         text={b.text}
         image={b.image}
        />
      </div>
    )
}

export default BigData;
