import React, { useEffect, useRef, useState } from 'react'; // Import the Component component from React
import {Link} from 'react-router-dom'; // Import the Link component
import ProjectDataLoader from './projectData.js';
import Tilt from './TiltPhaseSix'

function HomepageProject(props) {
  const projectImg = useRef(null);

  useEffect(() => {
    // add when mounted
    document.addEventListener("mousemove", shiftImage);
    // return function to be called when unmounted
    return () => {
      document.removeEventListener("mousemove", shiftImage);
    };
  }, []);

  const shiftImage = e => {
    projectImg.current.style.backgroundPositionX = -e.offsetX + "px";
    projectImg.current.style.backgroundPositionY = e.offsetY + "px";
  };


  const projectData = ProjectDataLoader();
  //console.log(projectData);

  for (var i =0; i<projectData.length; i++){
    if (projectData[i].key === props.projectKey){
      var project = projectData[i]
      //console.log(project)
      break
    }
  }

  return (
    <div className="" style={{width:"100%"}}>
      <Link to={project.link}>
        <div className="homepageProject"
          onMouseOver={e => (e.currentTarget.style.background = props.colour)}
          onMouseOut={e => (e.currentTarget.style.background = "#fffff2")}
        >
          <div className="homepageProject-img set-bg" ref={projectImg} style={{backgroundImage: "url(" + project.image + ")"}}/>
          <div className="hp-inner">
            <h3 className="project-number">{props.number+" / "+props.total}</h3>
            <h1 className="">{" "+project.title}</h1>
            <h2 className={"project-question"}>{project.question}</h2>
            <h4 className="fa fa-long-arrow-right strike"> see project</h4>
          </div>
        </div>
      </Link>
    </div>
  );
}

export default HomepageProject;
