import React from 'react'; // Import the Component component from React
import ScrollAnimation from 'react-animate-on-scroll';

class SectionOne extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      title:props.title,
      text:props.text,
      image:props.image,
      height:props.height
    }
  }

  render() {
    return(
      <ScrollAnimation animateIn="fadeIn">
      <div className="container-fluid p-md-0 ">
        <div className="row sectionOne justify-content-center">
          <div className='col-lg-6'>
            <h1 className="sectionOne-title">{this.state.title}</h1>
            <p className="sectionOne-text">{this.state.text}</p>
          </div>
          <img className="sectionOne-img col-lg-6" src={props.image}></img>
        </div>
      </div>
      </ScrollAnimation>
    )
  }
}

export default SectionOne;
