import React, { useEffect, useRef, useState } from 'react'; // Import the Component component from React
import Carousel from 'nuka-carousel';
import SlideImage from './../projectPage/slideImage.js'
import SlideInfo from './../projectPage/slideImage.js'

function ProjectCarousel(props) {
  // Fixes Carousel not loading initially
  const carouselRef = useRef(null)
  // Toggle slide info
  const [infoShow, changeInfoShow] = useState(false)

  useEffect(() => {
    setTimeout(() => {carouselRef.current.setDimensions(null);}, 0);
  }, [])

  // Changes slide info based on slide
  const [currentSlide, changeSlide] = useState(0);

  return(
    <div>
      <div className={"carouselWrapper "+ (infoShow ? null:"cwFull")}>
        <Carousel
          afterSlide={currentIndex => {changeSlide(currentIndex);}}
          enableKeyboardControls={true}
          slideIndex={currentSlide}
          disableEdgeSwiping={true}
          className="carousel"
          disableAnimation={false}
          heightMode="max"
          speed={500}
          width="100%"
          vertical={false}
          ref={carouselRef}
          renderCenterLeftControls={null}
          renderCenterRightControls={null}
          renderBottomLeftControls={({ previousSlide, slideCount }) => (
            <button
              className={`carouselPrev shift-left ${currentSlide===0 ? "invis":""}`}
              onClick={previousSlide}
              style={{left:"40%"}}
              >
              <i className="fa fa-long-arrow-left" />
            </button>
          )}
          renderBottomRightControls={({ nextSlide, slideCount }) => (
            <button
              className={`carouselNext ${currentSlide===slideCount-1 ? "shift-left":"shift-right"}`}
              onClick={currentSlide===slideCount-1 ? () => changeSlide(0):nextSlide}
              style={{right:"40%"}}
              >
              <i className={`${currentSlide===slideCount-1 ? "fa fa-repeat fa-flip-horizontal":"fa fa-long-arrow-right"}`}/>
            </button>
          )}
          defaultControlsConfig={{
            pagingDotsStyle: {
              fill: "#4A03FF"
            }
          }}
          >
          {props.slides}
        </Carousel>
      </div>
      <SlideInfo
        infoShow={infoShow}
        info={props.slideInfo[currentSlide]}
      />
      <button className="moreInfo" onClick={() => {changeInfoShow(!infoShow);}} style={{zIndex:"10"}}>{`${infoShow ? "Less Info":"More Info"}`}</button>
    </div>
  )
}

export default ProjectCarousel;
