import React, { useEffect, useRef, useState } from 'react'; // Import the Component component from React
import Carousel from 'nuka-carousel';
import '../css/App.css';
import HomepageProject from './homepageProject.js'

const project_total = "03"

function Home(props) {
  // Fixes Carousel not loading initially
  const carouselRef = useRef(null)
    useEffect(() => {
      setTimeout(() => {carouselRef.current.onResize();}, 0);
    }, [])

  const [currentSlide, changeSlide] = useState(0);

  return (
    <div>
      <Carousel
        afterSlide={currentIndex => {changeSlide(currentIndex);}}
        enableKeyboardControls={true}
        disableEdgeSwiping={true}
        heightMode="max"
        speed={500}
        width="100%"
        vertical={false}
        ref={carouselRef}
        >
        <HomepageProject
         projectKey={'panaseer'}
         key="01"
         number="01"
         total={project_total}
         colour="#0FFFB0"
        />
        <HomepageProject
         projectKey={'joyStory'}
         key="02"
         number="02"
         total={project_total}
         colour="#FF491C"
        />
        <HomepageProject
         projectKey={'spiro'}
         key="03"
         number="03"
         total={project_total}
         colour="#FFEE1C"
        />
      </Carousel>
      <span className="project-number">{"0"+(currentSlide+1).toString()+" / "+project_total}</span>
    </div>
  );
}

export default Home;
