import React, { useState, useEffect, useRef } from 'react';



function SlideImage(props) {

  const mobile = useWindowWidth() <= 991;

  const slide = useRef();
  const [fullscreen, toggleFullscreen] = useState(false);

  useEffect(() => {
    // add when mounted
    document.addEventListener("mousedown", handleClick);
    // return function to be called when unmounted
    return () => {
      document.removeEventListener("mousedown", handleClick);
    };
  }, []);

  const handleClick = e => {
    if (slide.current.contains(e.target)) {
      console.log(fullscreen)
      toggleFullscreen(true);
      return;
    }
  };

  return(
    <div className="slide" ref={slide} style={{backgroundImage:"url("+(mobile ? "" : props.image)+")"}}>
      <img src={mobile ? props.image : ""} style={{display:(mobile ? "" : "none")}} alt="default"/>
    </div>
  )
}


export default SlideImage;
