import React from 'react'; // Import the Component component from React

import ProjectHeader from './../projectPage/ProjectHeader.js'
import HeroVideo from './../projectPage/HeroVideo.js'
import SectionOne from './../projectPage/SectionOne.js'
import Image from './../projectPage/Image.js'

import Divider from './../projectPage/Divider.js';


const data = {
  'heroVideo':'videos/beatbox/hero.mp4',
}

const a = {
  'title':'Construction',
  'text':'We put the heart into a box that was painted black and the interior was lined with black felt. A wooden frame was made and glued into the interior of the box. A black felt screen hides the mechanism.',
  'image':require('./../../content/projects/beatbox/1.jpg'),
}

const b = {
  'title':'Coding',
  'text':'An Arduino Uno takes an input from the heart rate sensor and uses that to determine the PWM duty cycle of the motor: the faster the heartbeat, the faster the motor. The heart rate sensor output also controls the brightness of the LED strip.',
  'image':require('./../../content/projects/beatbox/2.jpg'),
}

const c = {
  'title':'Mechanism',
  'text':'The "heart plates" were hand moulded. Foil was then spray painted and glued to the plates. A motor is connected to a gear which drives a larger gear connected to a shaft. A snail cam on the shaft drives the heart plates and springs moves them back. ',
  'image':require('./../../content/projects/beatbox/2.jpg'),
}


function Beatbox(props) {
    return(
      <div>
        <ProjectHeader
         projectKey='beatbox'
        />
        <HeroVideo
         heroVide={data.heroVideo}
        />
        <Divider/>
        <SectionOne
         title={a.title}
         text={a.text}
         image={a.image}
        />
        <Divider/>
        <SectionOne
         title={b.title}
         text={b.text}
         image={b.image}
        />
      </div>
    )
}

export default Beatbox;
