import React from 'react'; // Import the Component component from React
import {Link} from 'react-router-dom'; // Import the Link component
import '../css/App.css';
import {CSSTransition} from 'react-transition-group';
import ReactPlayer from 'react-player'

const portfolioVideo = require("./../content/portfolio.mp4")

function Home(props) {
  return (
    <div className="container">
      <h1 className="home-text">I'm William, a design engineer creating
social, emotional and material value with technologies and systems</h1>
      <div className="highlight-reel">
        <ReactPlayer
         className='react-player'
         url={portfolioVideo}
         playing
         volume={10}
         muted
         loop
         height="600px"
         width='100%'
        />
      </div>
      <CSSTransition
        classNames="btnGrow"
        timeout={100}
      >
        <div className="btn-wrapper" style={{textAlign:"center"}}>
          <Link to="/projects" className="site-btn">See My Work</Link>
        </div>
      </CSSTransition>
    </div>
  );
}

export default Home;
