import React from 'react'; // Import the Component component from React
import SectionOne from './projectPage/SectionOne.js'
import Divider from './projectPage/Divider.js';
import Text from './projectPage/Text.js'
import AwesomeSlider from 'react-awesome-slider';
import 'react-awesome-slider/dist/styles.css';

const a = {
  'title':"Where I'm From",
  'text':'A keen interest in taking machines apart and trying to analyse and understand everything and everyone around me gave me a breadth of interest; never arbitrarily discriminating between Arts and Sciences but rather combining them.',
  'image':require('./../content/about/photo.jpeg'),
}

const b = {
  'title':"Where I Am",
  'text':'Studying Design Engineering at Imperial College London has given me the best of both worlds, developing in me a skillset of analytical, scientific rigour and empathetic, value-centred design thinking. Thinking with "two hats" has always been my style.',
}

const c = {
  'title':"Where I'm Going",
  'text':"Looking forward, I'm aiming to create as much value as I can in the world. Fields of interest to me are renewable energy, smart homes, internet of things (software and hardware) and wearable technology. I am concerned with increasing social value and improving the interface between man and machine as much as I am with increasing technical value.",
}

function About(props) {
    return(
      <div>
        <img src={a.image} style={{width:"100%",height:"100%"}}/>
        <h2>{a.text}</h2>
      </div>
    )
}

export default About;
