import React from 'react'; // Import the Component component from React
import {Link} from 'react-router-dom'; // Import the Link component
import '../css/App.css';
import HomepageProject from './homepageProject.js'
import Carousel from 'nuka-carousel';

const project_total = "03"

function Home(props) {
  const carouselRef = useRef(null)
    useEffect(() => {
      setTimeout(() => {carouselRef.current.setDimensions();}, 0);
    }, [])
  return (
    <Carousel
      enableKeyboardControls={true}
      disableEdgeSwiping={true}
      heightMode="max"
      speed={500}
      withoutControls={true}
      width="100%"
      vertical={true}
      ref={carouselRef}
      >
      <HomepageProject
       projectKey={'panaseer'}
       number="01"
       total={project_total}
       colour="#0FFFB0"
      />
      <HomepageProject
       projectKey={'joyStory'}
       number="02"
       total={project_total}
       colour="#FF491C"
      />
      <HomepageProject
       projectKey={'spiro'}
       number="03"
       total={project_total}
       colour="#FFEE1C"
      />
    </Carousel>
  );
}

export default Home;
