import React from 'react'; // Import the Component component from React
import Markdown from 'react-markdown'

class Text extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      col:props.col,
      height:props.height,
      text:props.text,
      align:props.align
    }
  }

  render() {
    return(
      <div className={"text-wrapper "+this.state.col}>
        <p className="sectionOne-text" style={{textAlign:this.state.align,height:this.state.height}}>{this.state.text}</p>
      </div>
    )
  }
}

export default Text;
