import React, { useEffect, useRef, useState } from 'react'; // Import the Component component from React
import { NavLink, Link } from 'react-router-dom';
import SocialBar from './SocialBar.js';

function Nav(props) {
  const [isExpanded, toggleExpanded] = useState(false)

  return (
    <div>
      <div className="logo-wrapper">
        <h1><NavLink to="/" activeClassName="activePage" className="logo">William Pepera</NavLink></h1>
      </div>
      <nav className="nav">
        <i
          className={`${isExpanded ? "fa fa-times" : "fa fa-bars"}`}
          aria-hidden="true"
          onClick={() => {toggleExpanded(!isExpanded);}}
        />
        <div className={`menu ${isExpanded ? "expanded" : "collapsed"}`}>
          <ul>
            <li><NavLink to="/projects" className="strikeNav" activeClassName="activePage" >Projects</NavLink></li>
            <li><NavLink to="/about" className="strikeNav" activeClassName="activePage" >About</NavLink></li>
            <li><a className='strikeNav' href="mailto:wpepera@icloud.com?Subject=Hello,%20William" target="_blank" rel="noopener noreferrer">Contact</a></li>
            <li><SocialBar/></li>
          </ul>
        </div>
      </nav>
    </div>
  );
}

export default Nav;
