import React from 'react'; // Import the Component component from React
import Markdown from 'react-markdown'

import projectDataLoader from './projectData.js';

class ProjectHeader extends React.Component {
  constructor(props) {
    super(props);

    const projectData = projectDataLoader();
    console.log(projectData)

    for (var i =0; i<projectData.length; i++){
      if (projectData[i].key === props.projectKey){
        var project = projectData[i]

        break
      }
    }

    this.state = {
      project:project
    }

  }

  render() {
    return(
      <div className='row projectPage-text'>
        <div className='col-lg-12'>
          <h1 >{this.state.project.title}</h1>
          <h2><Markdown source={this.state.project.question}/></h2>
        </div>
      </div>
    )
  }
}

export default ProjectHeader;
