import React from 'react'; // Import the Component component from React
import Markdown from 'react-markdown'
import ProjectDataLoader from './../projectData.js';
import ScrollAnimation from 'react-animate-on-scroll';

class ProjectHeader extends React.Component {
  constructor(props) {
    super(props);

    const projectData = ProjectDataLoader();
    //console.log(projectData);

    for (var i =0; i<projectData.length; i++){
      if (projectData[i].key === props.projectKey){
        var project = projectData[i]
        //console.log(project)
        break
      }
    }

    this.state = {
      project:project,
    }
  }

  render() {
    return(
      <ScrollAnimation animateIn="fadeIn">
        <div className='projectHeader'>
          <div className='row projectHeader-text'>
            <div className='col-lg-12'>
              <h1>{this.state.project.title}</h1>
              <h2><Markdown source={this.state.project.question}/></h2>
            </div>
          </div>
          <div className='row justify-content-center projectHeader-text'>
            <div className='col-lg-4'>
            </div>
            <div className='col-lg-4'>
              <h3>My Contribution:</h3>
              <h3><Markdown source={this.state.project.contribution}/></h3>
            </div>
            <div className='col-lg-4'>
              <h4>{this.state.project.month} {this.state.project.year}</h4>
            </div>
          </div>
        </div>
      </ScrollAnimation>
    )
  }
}

export default ProjectHeader;
