import React from 'react'; // Import the Component component from React
import Markdown from 'react-markdown'

import ProjectHeader from './../projectPage/ProjectHeader.js'
import HeroImage from './../projectPage/HeroImage.js'
import SectionOne from './../projectPage/SectionOne.js'
import PhoneVideo from './../projectPage/PhoneVideo.js'


const data = {
  'heroImage':require('./../../content/projects/balancingRobot/hero-image.png'),
  'video2':'videos/2.mp4',
  'video3':'videos/3.mp4',
}

const a = {
  'title':'Rhythm: Signal Processing',
  'text':'By measuring the energy of the sound signal, the beat of the song can be determined. On the beat, the energy of the sound peaks. The LED flash was then used to make sure that the program is detecting the beat correctly.',
  'video':'videos/1.mp4',
}

const b = {
  'title':'Balancing: PID Control',
  'text':'By using PID (Proportional, Integral, Derivative) control the robot can balance on its own by using readings from the accelerometer and gyroscope and controlling the motors to counteract the motion of the robot as it falls over. The angle of the robot is calculated by combining the accelerometer reading, which suffers from high frequency noise, and the gyroscope reading, which suffers from low frequency noise, or drift. Combining these two readings helps to offset this noise. Proportional control scales the motor speed to the difference between the current angle and the desired angle (0 degrees to remain upright): the greater the difference, the faster the motor spins to counteract the motion. Integral control sums the difference in the current and desired angle over time. The longer the robot angle has been away from the desired angle (and the greater that difference is), the greater the motor speed will be. Derivative control measures the rate of change of angle. The faster  the angle moves away from the desired angle, the faster the motor speed. In the video on the left, bluetooth is used to control the robot by changing the desired angle.',
  'video':'videos/1.mp4',
}

const c = {
  'title':'Dancing: Putting It All Together',
  'text':'Now that we are detecting the beat of the song and controlling the robot we can add some dance steps. By giving the robot some preset dance moves and arranging them into a list, the next dance move can be initiated on each beat. In the video on the left the robot performs its version of the traditional, Scottish "Strip the Willow" dance, wearing the proper attire of course.',
  'video':'videos/1.mp4',
}

function BalancingRobot(props) {
    return(
      <div>
        <ProjectHeader
         projectKey='balancingRobot'
        />
        <HeroImage
         heroImage={data.heroImage}
        />
        <PhoneVideo
          text={a.text}
          title={a.title}
          video={a.video}
        />
      </div>
    )
}

export default BalancingRobot;
