import React from 'react'; // Import the Component component from React
import ProjectCarousel from './../projectPage/projectCarousel.js'
import SlideImage from './../projectPage/slideImage.js'

function Embla(props) {
  const slides = [
    <SlideImage
      src={require('./../../content/projects/embla/hero-image.png')}
    />,
    <SlideImage
      src={require('./../../content/projects/embla/1.png')}
    />,
    <SlideImage
      src={require('./../../content/projects/embla/3.png')}
    />,
    <SlideImage
      src={require('./../../content/projects/embla/4.png')}
    />,
    <SlideImage
      src={require('./../../content/projects/embla/5.png')}
    />,
    <SlideImage
      src={require('./../../content/projects/embla/9.png')}
    />,
    <SlideImage
      src={require('./../../content/projects/embla/10.png')}
    />,
    <SlideImage
      src={require('./../../content/projects/embla/7.png')}
    />,
    <SlideImage
      src={require('./../../content/projects/embla/8.png')}
    />,
    <SlideImage
      src={require('./../../content/projects/embla/6.png')}
    />,
  ]

  const slideInfo = [
    {
      text:[],
      width:'35vw',
      top:'20vh',
    },
    {
      text:
        [
          'Workplace Stress',
          '',
        ],
      width:'30vw',
      top:'10vh',
    },
    {
      text:
        [
          'Matching Heart Rate',
          'An Arduino Uno takes an input from the heart rate sensor and uses that to determine the PWM duty cycle of the motor: the faster the heartbeat, the faster the motor. The heart rate sensor output also controls the brightness of the LED strip.',

        ],
      width:'30vw',
      top:'10vh',
    },
    {
      text:[
        'Pumping the Heart',
        'The "heart plates" were hand moulded. Foil was then spray painted and glued to the plates. A motor is connected to a gear which drives a larger gear connected to a shaft. A snail cam on the shaft drives the heart plates and springs moves them back. ',
      ],
      width:'30vw',
      top:'10vh',
    },
  ]

  return(
    <div>
      <ProjectCarousel
        projectKey='embla'
        slides={slides}
        slideInfo={slideInfo}
      />
    </div>
  )
}

export default Embla;
