import React from 'react';
import Thumbnail from './Thumbnail.js'; // Import the Thumbnail component
import '../css/App.css';

import {CSSTransition,TransitionGroup} from 'react-transition-group';

import ProjectDataLoader from './projectData.js';

class Projects extends React.Component {
  constructor(props) {
    super(props);

    const projectData = ProjectDataLoader();
    //console.log(projectData)

    this.state = {
      activeCategory:props.location.state.activeCategory,
      projects: projectData.sort((a,b) => parseInt(b.year)-parseInt(a.year)),
    }

    this.state.filterProjects=this.state.projects
    this.filter = this.filter.bind(this)
  }

  filter(category){
    this.setState({
      activeCategory:category,
      filterProjects:(category==='all' || category===null) ? this.state.projects : this.state.projects.filter(project => project.category.includes(category))
    });
  }

  render() {
    return (
        <section className="portfolio-section">
          <div className="portfolio-section-background set-bg" style={{backgroundImage:"url(" + require("./../content/background.png") + ")"}}/>
      		<div className="container">
      			<ul className="portfolio-filter">
      				<li className={`strikeNav ${(this.state.activeCategory==='all') ? "filter-active" : "filter-inactive"}`} onClick={() => this.filter('all')}>All</li>
      				<li className={`strikeNav ${(this.state.activeCategory==='cod') ? "filter-active" : "filter-inactive"}`} onClick={() => this.filter('cod')}>Coding and Electronics</li>
      				<li className={`strikeNav ${(this.state.activeCategory==='des') ? "filter-active" : "filter-inactive"}`} onClick={() => this.filter('des')}>Design</li>
      				<li className={`strikeNav ${(this.state.activeCategory==='eng') ? "filter-active" : "filter-inactive"}`} onClick={() => this.filter('eng')}>Engineering Analysis</li>
      				<li className={`strikeNav ${(this.state.activeCategory==='str') ? "filter-active" : "filter-inactive"}`} onClick={() => this.filter('str')}>Enterprise and Systems</li>
      			</ul>
      		</div>
          <TransitionGroup>
            <CSSTransition
                  classNames="portfolioFade"
                  timeout={800}
                  key={this.state.filterIndex}
              >
          		<div className="container-fluid p-md-0 ">
          			<div className="row portfolio-area" >
                  {this.state.filterProjects.map((thumbnail) => (
                    <Thumbnail
                      key={thumbnail.title}
                      link={thumbnail.link}
                      image={thumbnail.image}
                      title={thumbnail.title}
                      question={thumbnail.question}
                      col={thumbnail.col}
                      />
                  ))}
          			</div>
          		</div>
            </CSSTransition>
          </TransitionGroup>
      	</section>
    )
  }
}

export default Projects;
