import React, { useState, useEffect, useRef } from 'react';
import Thumbnail from './Thumbnail.js'; // Import the Thumbnail component
import {CSSTransition,TransitionGroup} from 'react-transition-group';
import ProjectDataLoader from './projectData.js';

function Projects(props) {
  const projects = ProjectDataLoader()

  const [activeCategory, updateCategory] = useState(props.location.activeCategory)

  const [filterProjects, updateProjects] = useState(projects.sort((a,b) => parseInt(b.year)-parseInt(a.year)))

  useEffect(() => {
    console.log(activeCategory)
    updateProjects((activeCategory==='all' || activeCategory===null || activeCategory===undefined) ? projects : projects.filter(project => project.category.includes(activeCategory)))
  },[activeCategory]);

  return (
      <section className="portfolio-section">
        <div className="portfolio-section-background set-bg" style={{backgroundImage:"url(" + require("./../content/background.png") + ")"}}/>
    		<div className="container">
    			<ul className="portfolio-filter">
    				<li className={`strikeNav ${(activeCategory==='all') ? "filter-active" : "filter-inactive"}`} onClick={() => updateCategory('all')}>All</li>
    				<li className={`strikeNav ${(activeCategory==='cod') ? "filter-active" : "filter-inactive"}`} onClick={() => updateCategory('cod')}>Coding and Electronics</li>
    				<li className={`strikeNav ${(activeCategory==='des') ? "filter-active" : "filter-inactive"}`} onClick={() => updateCategory('des')}>Design</li>
    				<li className={`strikeNav ${(activeCategory==='eng') ? "filter-active" : "filter-inactive"}`} onClick={() => updateCategory('eng')}>Engineering Analysis</li>
    				<li className={`strikeNav ${(activeCategory==='str') ? "filter-active" : "filter-inactive"}`} onClick={() => updateCategory('str')}>Enterprise and Systems</li>
    			</ul>
    		</div>
        <TransitionGroup>
          <CSSTransition
                classNames="portfolioFade"
                timeout={800}
                key={0}
            >
        		<div className="container-fluid p-md-0 ">
        			<div className="row portfolio-area" >
                {filterProjects.map((thumbnail) => (
                  <Thumbnail
                    key={thumbnail.title}
                    link={thumbnail.link}
                    image={thumbnail.image}
                    title={thumbnail.title}
                    question={thumbnail.question}
                    col={thumbnail.col}
                    />
                ))}
        			</div>
        		</div>
          </CSSTransition>
        </TransitionGroup>
    	</section>
  )
}

export default Projects;
