import React from 'react';
import logo from './logo.svg';

import { BrowserRouter, Route, Link, Switch } from 'react-router-dom';
import Projects from './components/Projects.js';
import Articles from './components/Articles.js';
import About from './components/About.js';
import Contact from './components/Contact.js';

import {CSSTransition,TransitionGroup} from 'react-transition-group';

// Styles
import './css/App.css';
import './css/bootstrap.min.css';

function App() {
  return (
    <BrowserRouter>
      <Route render={({ location }) => (
        <div className="App">
          <header className="header-section">
            <div className="container">
              <div className="row">
                <div className="col-lg-4 col-md-3">
                  <div className="logo">
                    <h2 className="site-logo">William Pepera</h2>
                  </div>
                </div>
                <div className="col-lg-6 col-md-3 col-sm-1">
                  <nav className="main-menu">
                    <ul>
                      <li><Link to="/" className={(location.pathname==='/')?"activePage":"inactivePage"}>Projects</Link></li>
                      <li><Link to="/articles" className={(location.pathname==='/articles')?"activePage":"inactivePage"}>Articles</Link></li>
                      <li><Link to="/about" className={(location.pathname==='/about')?"activePage":"inactivePage"}>About</Link></li>
                      <li><Link to="/contact" className={(location.pathname==='/contact')?"activePage":"inactivePage"}>Contact</Link></li>
                    </ul>
                  </nav>
                </div>
              </div>
            </div>
          </header>

          <TransitionGroup>
            <CSSTransition
                key={location.key}
                timeout={500}
                classNames='page'
                >
                <Switch location={location}>
                  <Route exact path="/" component={Projects} />
                  <Route path="/articles" component={Articles} />
                  <Route path="/about" component={About} />
                  <Route path="/contact" component={Contact} />
                </Switch>
            </CSSTransition>
          </TransitionGroup>
        </div>
      )}/>
    </BrowserRouter>
  );
}

export default App;
