import React from 'react'; // Import the Component component from React
import {Link} from 'react-router-dom'; // Import the Link component
import '../css/App.css';

import Markdown from 'react-markdown'
import Divider from './projectPage/Divider.js';
import Image from './projectPage/Image.js'
import Text from './projectPage/Text.js'

function ArticleThumbnail(props) {
  return (
    <Link to={props.link}>
      <div className="row justify-content-center">
        <Divider/>
        <Image
         image={props.image}
         col={props.imgCol}
         height="auto"
         margin="0px 0px 0px 0px"
        />
        <div className={props.textCol}>
          <div className="row justify-content-center">
            <Text
             text={props.title}
             col={props.textCol}
             align="left"
             type="sectionOne-title"
             font="20px"
             margin="0px 0px 20px 010px"
            />
          </div>
          <div className="row justify-content-center">
            <Text
             text={props.text}
             col={props.textCol}
             align="left"
             type="text"
             font="15px"
             margin="0px 0px 0px 0px"
            />
          </div>
        </div>
      </div>
    </Link>
  );
}

export default ArticleThumbnail;
