import React from "react"
import ReactMixitup from 'react-mixitup'
import Thumbnail from './Thumbnail.js'; // Import the Thumbnail component
import '../css/App.css';
import mixitup from 'mixitup';

class Projects extends React.Component {
  constructor(props) {
    super(props);

    // Initialize with some "photos" that are cached (or none at all). Maybe you
    // have a service worker that cached the last API response and you can
    // use that here while waiting on a network request.
    const grayPixel = 'data:image/gif;base64,R0lGODlhAQABAIAAAMLCwgAAACH5BAAAAAAALAAAAAABAAEAAAICRAEAOw==';
    const blackPixel = 'data:image/gif;base64,R0lGODlhAQABAIAAAAUEBAAAACwAAAAAAQABAAACAkQBADs=';
    const greenPixel = 'data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAADUlEQVR42mO02Vz4HwAE9AJhcLBN6AAAAABJRU5ErkJggg==';

    this.state = {
      photos: [
        { id: 1, src: grayPixel },
        { id: 2, src: blackPixel },
        { id: 3, src: greenPixel },
      ],
    };

    this.element = React.createRef();
    this.sizer = React.createRef();
  }

  render() {
    return (
      <section className="portfolio-section">
    		<div className="container">
    			<ul className="portfolio-filter controls">
    				<li className="control" data-filter="all">All</li>
    				<li className="control" data-filter=".web">Coding</li>
    				<li className="control" data-filter=".digital">Electronics</li>
    				<li className="control" data-filter=".rened">Mechanical Engineering</li>
    				<li className="control" data-filter=".brand">Strategy and Management</li>
    			</ul>
    		</div>
    		<div className="container-fluid p-md-0 ">
    			<div className="row portfolio-area">
              <Thumbnail
                link="/embla"
                image={require('./../content/projects/embla/thumbnail.png')}
                title="Embla"
                category=""
                col="col-md-8"
              />
              <Thumbnail
                link="/beatbox"
                image={require('./../content/projects/beatbox/thumbnail.jpg')}
                title="Beatbox"
                category=""
                col="col-md-4"
              />
    			</div>
    		</div>
    	</section>
    )
  }
}

export default Projects;
