import React from 'react'; // Import the Component component from React

import ProjectHeader from './../projectPage/ProjectHeader.js'
import HeroImage from './../projectPage/HeroImage.js'
import SectionOne from './../projectPage/SectionOne.js'
import Image from './../projectPage/Image.js'
import Text from './../projectPage/Text.js'


import Divider from './../projectPage/Divider.js';


const data = {
  'heroImage':require('./../../content/projects/pause/hero-image.png'),
}

const a = {
  'title':'Breaking the Flow',
  'text':"Pause is an app, targeted at adolescents, that aims to address excessive use of social networking sites by empowering the user to control their screen time. The longer the user scrolls, the longer content takes to load and the harder scrolling becomes, breaking the flow. This break is leveraged as a positive nudge to stop scrolling. Scrolling and loading speed will slowly recover as a reward when social media isn't being used. Pause is inspired by the EAST framework, making scrolling less easy and by using social and timely intervenions",
  'image':require('./../../content/projects/pause/1.png'),
}

const b = {
  'col':'col-2',
  'height':'auto',
  'image':require('./../../content/projects/pause/2.png'),
  'image2':require('./../../content/projects/pause/3.png'),
  'image3':require('./../../content/projects/pause/4.png'),
  'image4':require('./../../content/projects/pause/5.png'),
  'image5':require('./../../content/projects/pause/6.png'),
  'image6':require('./../../content/projects/pause/7.png'),
  'image7':require('./../../content/projects/pause/8.png'),
  'image8':require('./../../content/projects/pause/9.png'),
}

const c = {
  'image':require('./../../content/projects/pause/10.png'),
  'image2':require('./../../content/projects/pause/18.png'),
  'image3':require('./../../content/projects/pause/12.png'),
  'image4':require('./../../content/projects/pause/13.png'),
}

const d = {
  'image':require('./../../content/projects/pause/14.png'),
  'image2':require('./../../content/projects/pause/15.png'),
  'image3':require('./../../content/projects/pause/16.png'),
  'image4':require('./../../content/projects/pause/17.png'),
}




function Pause(props) {
    return(
      <div>
        <ProjectHeader
         projectKey='pause'
        />
        <HeroImage
         heroImage={data.heroImage}
        />
        <Divider/>
        <Text
         text='Concept'
         col="col-12"
         type="title"
        />
        <SectionOne
         title={a.title}
         text={a.text}
         image={a.image}
        />
        <Text
         text="Pause is not designed to merely decrease social media use, it is designed to help users get the most out of social media. Engaging with others online can be beneficial. Pause aims to reduce mindless scrolling through 'feeds'. By encourgaging users to engage with others, Pause makes slowing down on social media a social activity in itself."
         col="col-12"
         align="left"
         type="sectionOne-text"
         font="25px"
         margin="0px 10px 150px 85px"
        />
        <Divider/>
        <Text
         text='App Mockup'
         col="col-12"
         type="title"
        />
        <Text
         text='Inital Setup'
         type="sectionOne-title"
        />
        <div className="row justify-content-center">
          <Image
           image={b.image}
           col={b.col}
           height={b.height}
           margin="50px 0px 20px 0px"
           padding="0px 0px 0px 0px"
          />
          <Image
           image={b.image2}
           col={b.col}
           height={b.height}
           margin="50px 25px 20px 50px"
           padding="0px 0px 0px 0px"
          />
          <Image
           image={b.image3}
           col={b.col}
           height={b.height}
           margin="50px 50px 20px 25px"
           padding="0px 0px 0px 0px"
          />
          <Image
           image={b.image4}
           col={b.col}
           height={b.height}
           margin="50px 0px 20px 0px"
           padding="0px 0px 0px 0px"
          />
        </div>
        <div className="row justify-content-center">
          <Image
           image={b.image5}
           col={b.col}
           height={b.height}
           margin="50px 0px 20px 0px"
           padding="0px 0px 0px 0px"
          />
          <Image
           image={b.image6}
           col={b.col}
           height={b.height}
           margin="50px 25px 20px 50px"
           padding="0px 0px 0px 0px"
          />
          <Image
           image={b.image7}
           col={b.col}
           height={b.height}
           margin="50px 50px 20px 25px"
           padding="0px 0px 0px 0px"
          />
          <Image
           image={b.image8}
           col={b.col}
           height={b.height}
           margin="50px 0px 200px 0px"
           padding="0px 0px 0px 0px"
          />
        </div>
        <SectionOne
         title="Main Screen"
         text="Contains a visual representation of remaining scroll time. Highlights remaining time, rather than used time, motivating the user to use their remaining time wisely. Includes a streak for how many days they have stayed on target, photos of their Friends (with a button to add more), and navigation buttons."
         image={c.image}
         height="auto"
        />
        <SectionOne
         title="Statistics"
         text="Shows progress over time and explains what data is being recorded and how it’s being used."
         image={c.image2}
         height="auto"
        />
        <SectionOne
         title="Notification"
         text="When the user reaches their scroll time limit, this message box appears. It is timely (once the user has reached their self-defined limit) and social (it encourages users to engage with others in using social media less)."
         image={c.image3}
         height="auto"
        />
        <SectionOne
         title="Friends"
         text="Allows users to manage which Friends they have added in Pause. These could be peers, parents, teachers etc. with whom the user is engaging in managing their social media use."
         image={c.image4}
         height="auto"
        />
        <Text
         text='Settings'
         type="sectionOne-title"
        />
        <div className="row justify-content-center">
          <Image
           image={d.image}
           col={b.col}
           height={b.height}
           margin="50px 0px 20px 0px"
           padding="0px 0px 0px 0px"
          />
          <Image
           image={d.image2}
           col={b.col}
           height={b.height}
           margin="50px 25px 20px 50px"
           padding="0px 0px 0px 0px"
          />
          <Image
           image={d.image3}
           col={b.col}
           height={b.height}
           margin="50px 50px 20px 25px"
           padding="0px 0px 0px 0px"
          />
          <Image
           image={d.image4}
           col={b.col}
           height={b.height}
           margin="50px 0px 200px 0px"
           padding="0px 0px 0px 0px"
          />
        </div>
      </div>

    )
}

export default Pause;
