import React, { useEffect, useRef, useState } from 'react'; // Import the Component component from React
import Carousel from 'nuka-carousel';
import '../css/App.css';
import HomepageProject from './homepageProject.js'

const project_total = "03"

function Home(props) {
  const [currentSlide, changeSlide] = useState(0);

  // Fixes Carousel not loading initially
  const carouselRef = useRef(null)
    useEffect(() => {
      setTimeout(() => {carouselRef.current.onResize();}, 0);
    }, [])

  return (
    <Carousel
      afterSlide={(currentIndex,slideCount) => {changeSlide(currentIndex);}}
      slideIndex={currentSlide}
      enableKeyboardControls={true}
      disableEdgeSwiping={true}
      heightMode="max"
      speed={500}
      width="100%"
      vertical={false}
      ref={carouselRef}
      renderCenterLeftControls={({ previousSlide, slideCount }) => (
        <button className={`carouselPrev ${currentSlide===0 ? "invis":""}`} onClick={previousSlide}>
          <i className="fa fa-long-arrow-left" />
        </button>
      )}
      renderCenterRightControls={({ nextSlide, slideCount }) => (
        <button className="carouselNext" onClick={currentSlide===slideCount-1 ? () => changeSlide(0):nextSlide}>
          <i className={`${currentSlide===slideCount-1 ? "fa fa-repeat fa-flip-horizontal":"fa fa-long-arrow-right"}`}/>
        </button>
      )}
      defaultControlsConfig={{
        pagingDotsStyle: {
          fill: "#4A03FF"
        }
      }}
      >
      <HomepageProject
       projectKey={'panaseer'}
       key="01"
       number="01"
       total={project_total}
       colour="#0FFFB0"
      />
      <HomepageProject
       projectKey={'joyStory'}
       key="02"
       number="02"
       total={project_total}
       colour="#FF491C"
      />
      <HomepageProject
       projectKey={'spiro'}
       key="03"
       number="03"
       total={project_total}
       colour="#FFEE1C"
      />
    </Carousel>
  );
}

export default Home;
