import React from 'react'; // Import the Component component from React
import ScrollAnimation from 'react-animate-on-scroll';

class SectionTwo extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      title:props.title,
      text:props.text,
      image:props.image
    }
  }

  render() {
    return(
      <ScrollAnimation animateIn="fadeIn">
        <div className="container-fluid p-md-0 ">
          <div className="row sectionOne">
            <div className='col-lg-5'>
              <h1 className="sectionOne-title">{this.state.title}</h1>
              <p className="sectionOne-text">{this.state.text}</p>
            </div>
            <div className="col-lg-7 sectionOne-img">
              <img src={this.state.image} alt="default"/>
            </div>
          </div>
        </div>
      </ScrollAnimation>
    )
  }
}

export default SectionTwo;
