import React from 'react';
import ArticleThumbnail from './ArticleThumbnail.js'; // Import the Thumbnail component
import '../css/App.css';

import {CSSTransition,TransitionGroup} from 'react-transition-group';

import ArticleDataLoader from './articleData.js';

class Articles extends React.Component {
  constructor(props) {
    super(props);

    const articleData = ArticleDataLoader();

    this.state = {
      articles:articleData
    }
  }


  render() {
    return (
        <section className="article-section">
          <TransitionGroup>
            <CSSTransition
                    classNames="portfolioFade"
                    timeout={800}
                    key={this.state.filterIndex}
                  >
          		<div className="container-fluid p-md-0 ">
                {this.state.articles.map((thumbnail) => (
                  <ArticleThumbnail
                    key={thumbnail.title}
                    link={thumbnail.link}
                    image={thumbnail.image}
                    title={thumbnail.title}
                    text={thumbnail.description}
                    />
                ))}
          		</div>
            </CSSTransition>
          </TransitionGroup>
      	</section>

    )
  }
}

export default Articles;
