import React, { Component } from 'react'; // Import the Component component from React

import { NavLink, Link } from 'react-router-dom';
import SocialBar from './SocialBar.js';

import styled from "styled-components";


const Navigation = styled.header`
  min-width:100%;
  height:auto;
  z-index: 1;
  display: flex;

  &:hover{
    background:white;
  }

  a {
    color: #979797;
    font-size: 1em;
  }

  a:hover {
    color:#4A03FF;
  }

  .logo-wrapper a{
    position:absolute;
    top:20%;
    left:5%;
    margin:auto;
  }

  .logo {
    font-size: 24px;
    font-weight: 200;
    margin:auto;
  }

  nav {
    margin:auto;

    ul {
      display: flex;
    }

    li {
      padding: 14px 16px;
    }

    a{
      font-size: 1.4em;
      text-decoration: none;
    }

    .activePage{
      color:#4A03FF;
    }
  }

  .social-wrapper{
    position:absolute;
    margin:auto;
    top:10%;
    right:5%;
    flex-direction: column;
    justify-content: space-between;
    flex-wrap: wrap;
    overflow: hidden;

  }

  .social-links a {
    color: #979797;
    font-size: 2em;
    padding: 0px 16px;
  }

  .social-links a:hover{
    color: #4A03FF;
  }

  .fa-bars,.fa-times{
    display: none;
    color: #979797;
    font-size: 2rem;
  }

  .fa-bars:hover,.fa-times:hover{
    color:#4A03FF;
  }



  @media only screen and (max-width: 1240px) {
    height: auto;
    min-height: 50px;
    display: block;
    position: relative;

    .fa-bars,.fa-times{
      display: inline-block;
      position: absolute;
      top: 10px;
      right: 10px;
      cursor: pointer;
    }

    .logo-wrapper a{
      float: none;
      position: absolute;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
    }

    .logo {
      font-size: 18px;
      text-align: center;
      position: absolute;
      top: 0px
    }

    .logo-hidden {
      display:none;

    }

    nav {
      ul {
        display: block;
      }

      li {
        font-size: 1em;
      }
    }

    .social-hidden{
      display:none;
    }

    .social-links a {
      font-size: 2em;
      padding:0px 10px;
    }

    .social-links a:last-child {
      padding-right:50px;
    }

    div.collapsed {
      width: 100%;
      display: inline;
      flex-direction: column;
      justify-content: space-between;
      flex-wrap: wrap;
      overflow: hidden;
      max-height: 0;

      &.is-expanded {
        overflow: hidden;
        max-height: 300px; /* approximate max height */
        -moz-transition-duration: 0.5s;
        -webkit-transition-duration: 0.5s;
        -o-transition-duration: 0.5s;
        transition-duration: 0.5s;
        -moz-transition-timing-function: ease-in;
        -webkit-transition-timing-function: ease-in;
        -o-transition-timing-function: ease-in;
        transition-timing-function: ease-in;
      }

      li {
        padding: 15px 10px;
        margin: 0px 0px;
      }
    }
  }
`;


class Nav extends Component {
  constructor(props) {
    super(props);
    this.node = React.createRef();
    this.state = {
      isExpanded: false
    };
  }

  handleToggle(e) {
    this.setState({
      isExpanded: !this.state.isExpanded
    });
  }

  componentWillMount(){
    document.addEventListener('mousedown',this.handleClick,false);
  }

  componentWillUnmount(){
    document.removeEventListener('mousedown',this.handleClick,false);
  }

  handleClick = (e) => {
    if (!this.node.contains(e.target)){
      this.setState({
        isExpanded: false
      });
    }
  }

  render() {
    const { isExpanded } = this.state;

    return (
      <Navigation>
        <div className="logo-wrapper">
          <h2><Link to="/" className={`${isExpanded ? "logo-hidden" : "logo"}`}>William Pepera</Link></h2>
        </div>
        <nav className="nav" ref={node => this.node = node}>
          <i
            className={`${isExpanded ? "fa fa-times" : "fa fa-bars"}`}
            aria-hidden="true"
            onClick={e => this.handleToggle(e)}
          />
          <div className={`collapsed ${isExpanded ? "is-expanded" : ""}`}>
            <ul>
              <li><NavLink to="/projects" activeClassName="activePage" onClick={e => this.handleToggle(e)}>Projects</NavLink></li>
              <li><NavLink to="/articles" activeClassName="activePage" onClick={e => this.handleToggle(e)}>Articles</NavLink></li>
              <li><NavLink to="/about" activeClassName="activePage" onClick={e => this.handleToggle(e)}>About</NavLink></li>
              <li><a href="https://www.linkedin.com/in/william-pepera-15978598/" target="_blank" rel="noopener noreferrer">Contact</a></li>
            </ul>
          </div>
        </nav>
        <div className={`social-wrapper ${isExpanded ? "" : "social-hidden"}`}>
          <SocialBar/>
        </div>
      </Navigation>
    );
  }
}

export default Nav;
