import React from 'react'; // Import the Component component from React
import ScrollAnimation from 'react-animate-on-scroll';

class Text extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      col:props.col,
      text:props.text,
      align:props.align,
      font:props.font,
      type:props.type,
      weight:props.weight,
      color:props.color,
      margin:props.margin,
      wrapperMargin:props.wrapperMargin,
    }
  }

  render() {
    return(
        <div className={this.state.col} style={{margin:this.state.wrapperMargin}}>
          <ScrollAnimation animateIn="fadeIn">
            <p className={this.state.type} style={{textAlign:this.state.align,fontWeight:this.state.weight,fontSize:this.state.font,color:this.state.color,margin:this.state.margin}}>{this.state.text}</p>
          </ScrollAnimation>
        </div>

    )
  }
}

export default Text;
