import React from 'react'; // Import the Component component from React

class Image extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      image:props.image,
      col:props.col,
      height:props.height,
      margin:props.margin,
      padding:props.padding,
    }
  }

  render() {
    return(
      <div className={"image-wrapper "+this.state.col} style={{margin:this.state.margin}}>
        <img src={this.state.image} style={{height:this.state.height,padding:this.state.padding}} alt="default"/>
      </div>
    )
  }
}

export default Image;
