import React, { useState, useEffect, useRef } from 'react';

function SlideInfo(props) {

  const [isExpanded, toggleExpanded] = useState(false);

  const width = useWindowWidth()

  useEffect(() => {
    // add when mounted
    document.addEventListener("mousemove", handleMove);
    // return function to be called when unmounted
    return () => {
      document.removeEventListener("mousemove", handleMove);
    };
  }, []);

  const handleMove = e => {
    console.log(e.offsetX)
    console.log(width*0.7)
    if ((e.offsetX > Math.abs(width*0.3)) && !isExpanded) {
      toggleExpanded(true)
    } else if (e.offsetX < Math.abs(width*0.7)) {
      toggleExpanded(false)
    }
  }

  return(
    <div className='infoIcon' style={{right:`${isExpanded ? '30vw':'2vw'}`}}>
      <i
        className={`${isExpanded ? "fa fa-chevron-right" : "fa fa-chevron-left"}`}
        aria-hidden="true"
        onClick={() => {toggleExpanded(!isExpanded);}}

      />
      <div className={`slideInfo ${isExpanded ? "expanded":"collapsed"}`}>
        <h1>{props.info[0]}</h1>
        <h2>{props.info[1]}</h2>
      </div>
    </div>
  )
}

const useWindowWidth = () => {
  const [windowWidth, setWindowWidth ] = useState(window.innerWidth);

  useEffect(() => {
      const handleWindowResize = () => {
          setWindowWidth(window.innerWidth);
      };

      window.addEventListener('resize', handleWindowResize);
      return () => window.removeEventListener('resize', handleWindowResize);
  },[windowWidth]);

  return windowWidth;
};

export default SlideInfo;
