import React, { useState } from 'react';
import Thumbnail from './Thumbnail.js'; // Import the Thumbnail component
import '../css/App.css';

import {CSSTransition,TransitionGroup} from 'react-transition-group';

class Projects extends React.Component {
  constructor(props) {
    super(props);

    const embla = <Thumbnail
      link="/embla"
      image={require('./../content/projects/embla/thumbnail.png')}
      key="Embla"
      category=""
      col="col-md-8"/>

    const beatbox = <Thumbnail
      link="/beatbox"
      image={require('./../content/projects/beatbox/thumbnail.jpg')}
      key="Beatbox"
      category=""
      col="col-md-4"/>

    this.state = {
      display: false,
      projects: [
        embla,
        beatbox
      ]
    }
  }

  render() {
    return (
      <CSSTransition
        in={true}
        timeout={300}
        unmountOnExit
        appear
        classNames="portfolio">
        <section className="portfolio-section">
      		<div className="container">
      			<ul className="portfolio-filter controls">
      				<li className="control" data-filter="all">All</li>
      				<li className="control" data-filter=".web">Coding</li>
      				<li className="control" data-filter=".digital">Electronics</li>
      				<li className="control" data-filter=".rened">Mechanical Engineering</li>
      				<li className="control" data-filter=".brand">Strategy and Management</li>
      			</ul>
      		</div>
      		<div className="container-fluid p-md-0 ">
      			<div className="row portfolio-area">
                {this.state.projects}
      			</div>
      		</div>
      	</section>
      </CSSTransition>
    )
  }
}

export default Projects;
