import React, { useState } from 'react';
import Thumbnail from './Thumbnail.js'; // Import the Thumbnail component
import '../css/App.css';

import {CSSTransition,TransitionGroup} from 'react-transition-group';


class Projects extends React.Component {
  constructor(props) {
    super(props);

    const embla = {
      'link':"/embla",
      'image':require('./../content/projects/embla/thumbnail.png'),
      'title':"Embla",
      'col':"col-md-8",
      'category':['str']
    }

    const beatbox = {
      'link':"/beatbox",
      'image':require('./../content/projects/beatbox/thumbnail.jpg'),
      'title':"Beatbox",
      'col':"col-md-4",
      'category':['cod', 'ele']
    }

    this.state = {
      activeFilter:[true,false,false,false,false],
      projects: [
        embla,
        beatbox
      ],
    }

    this.state.filterProjects=this.state.projects

    this.filter = this.filter.bind(this)
  }

  filter(i, category){
    var activeFilter = [false,false,false,false,false];
    activeFilter[i] = true;
    this.setState({
      activeFilter:activeFilter,
      filterProjects:(category=='all') ? this.state.projects : this.state.projects.filter(project => project.category.includes(category))
    });
  }

  render() {
    const activeFilter = this.state.activeFilter
    const filterProjects = this.state.filterProjects
    return (
        <section className="portfolio-section">
      		<div className="container">
      			<ul className="portfolio-filter">
      				<li className={activeFilter[0]? "filter-active" : "filter-inactive"} onClick={() => this.filter(0, 'all')}>All</li>
      				<li className={activeFilter[1]? "filter-active" : "filter-inactive"} onClick={() => this.filter(1, 'cod')}>Coding</li>
      				<li className={activeFilter[2]? "filter-active" : "filter-inactive"} onClick={() => this.filter(2, 'ele')}>Electronics</li>
      				<li className={activeFilter[3]? "filter-active" : "filter-inactive"} onClick={() => this.filter(3, 'mec')}>Mechanical Engineering</li>
      				<li className={activeFilter[4]? "filter-active" : "filter-inactive"} onClick={() => this.filter(4, 'str')}>Strategy and Systems</li>
      			</ul>
      		</div>
          <CSSTransition
                  classNames="portfolioFade"
                  timeout={300}
                >
        		<div className="container-fluid p-md-0 ">
        			<div className="row portfolio-area">
                {filterProjects.map((thumbnail) => (
                  <Thumbnail
                    link={thumbnail.link}
                    image={thumbnail.image}
                    title={thumbnail.title}
                    col={thumbnail.col}
                    />
                ))}
        			</div>
        		</div>
          </CSSTransition>
      	</section>

    )
  }
}

export default Projects;
