import React, { useEffect, useRef, useState } from 'react'; // Import the Component component from React
import Carousel from 'nuka-carousel';

function ProjectCarousel(props) {
  // Fixes Carousel not loading initially
  const carouselRef = useRef(null)
  // Toggle slide info
  const [infoShow, changeInfoShow] = useState(true)
  const [disableAnimate, changeDisableAnimate] = useState(false)

  useEffect(() => {
    changeDisableAnimate(true)
    setTimeout(() => {carouselRef.current.onResize(); changeDisableAnimate(false);}, 300);

  }, [infoShow])

  // Changes slide info based on slide
  const [currentSlide, changeSlide] = useState(0);
  const [infoOpacity, changeOpacity] = useState(1);



  console.log(infoShow)


  return(
    <div>
      <div className={"carouselWrapper "+ (infoShow ? null:"cwFull")}>
        <button className="moreInfo" onClick={() => {changeInfoShow(!infoShow);}} style={{zIndex:"10"}}>More Info</button>
        <Carousel
          afterSlide={currentIndex => {changeSlide(currentIndex);  changeOpacity(1);}}
          enableKeyboardControls={true}
          disableEdgeSwiping={true}
          disableAnimation={disableAnimate}
          heightMode="max"
          speed={500}
          width="100%"
          vertical={false}
          ref={carouselRef}
          renderCenterLeftControls={({ previousSlide, slideCount }) => (
            <button className={`carouselPrev ${currentSlide===0 ? "invis":""}`} onClick={previousSlide}>
              <i className="fa fa-long-arrow-left" />
            </button>
          )}
          renderCenterRightControls={({ nextSlide, slideCount, slideIndex }) => (
            <button className={`${currentSlide===slideCount-1 ? "carouselNext ":"carouselBeginning "}`} onClick={currentSlide===slideCount-1 ? nextSlide:() => slideIndex=0}>
              <i className="fa fa-long-arrow-right"/>
            </button>
          )}
          defaultControlsConfig={{
            pagingDotsStyle: {
              fill: "#4A03FF"
            }
          }}
          >
          {props.slides}
        </Carousel>
      </div>
      <div className={'slideInfo ' + (infoShow ? null:"none")} style={{opacity:infoOpacity}}>
        {props.slideInfo[currentSlide]}
      </div>
    </div>
  )
}

export default ProjectCarousel;
