import React from 'react';
import logo from './logo.svg';

import { BrowserRouter, Route, Link, Switch } from 'react-router-dom';
import Home from './components/Home.js';
import Projects from './components/Projects.js';
import Articles from './components/Articles.js';
import About from './components/About.js';
import Contact from './components/Contact.js';

//Projects
import BalancingRobot from './components/projects/balancingRobot.js'

import SocialBar from './components/SocialBar.js';

import {CSSTransition,TransitionGroup} from 'react-transition-group';

// Styles
import './css/App.css';
import './css/bootstrap.min.css';


function App() {
  return (
    <BrowserRouter>
      <Route render={({ location }) => (
        <div className="App">
          <header className="header-section">
            <div className="container">
              <div className="row">
                <div className="col-lg-4 col-md-3">
                  <div className="logo">
                    <h2><Link to="/" className="site-logo">William Pepera</Link></h2>
                  </div>
                </div>
                <div className="col-lg-4 col-md-4 col-4">
                  <nav className="main-menu row">
                    <ul>
                      <li><Link to="/projects" className={(location.pathname==='/projects')?"activePage":"inactivePage"}>Projects</Link></li>
                      <li><Link to="/articles" className={(location.pathname==='/articles')?"activePage":"inactivePage"}>Articles</Link></li>
                      <li><Link to="/about" className={(location.pathname==='/about')?"activePage":"inactivePage"}>About</Link></li>
                      <li><Link to="/contact" className={(location.pathname==='/contact')?"activePage":"inactivePage"}>Contact</Link></li>
                    </ul>
                  </nav>
                </div>
                <div className="col-lg-4 col-md-5 col-5">
                  <div style={{textAlign:'right'}}>
                    <SocialBar/>
                  </div>
                </div>
              </div>
            </div>
          </header>

          <TransitionGroup>
            <CSSTransition
                key={location.key}
                timeout={800}
                classNames='page'
                >
                <Switch location={location}>
                  <Route exact path="/" component={Home} />
                  <Route exact path="/projects" component={Projects} />
                  <Route path="/articles" component={Articles} />
                  <Route path="/about" component={About} />
                  <Route path="/contact" component={Contact} />
                  <Route path="/balancingRobot" component={BalancingRobot} />
                </Switch>
            </CSSTransition>
          </TransitionGroup>
          <footer className="footer-section text-center">
            <div className="container">
              <div className="copyright">
                Built with React.js       Copyright &copy; 2020
              </div>
            </div>
          </footer>
        </div>
      )}/>
    </BrowserRouter>
  );
}

export default App;
