import React from 'react'; // Import the Component component from React
import Markdown from 'react-markdown'

import ProjectHeader from './../projectPage/ProjectHeader.js'
import HeroImage from './../projectPage/HeroImage.js'
import SectionOne from './../projectPage/SectionOne.js'

import ReactPlayer from 'react-player'

const data = {
  'heroImage':require('./../../content/projects/balancingRobot/hero-image.png'),
  'video1':'videos/1.mp4'
}

const a = {
  'title':'Rhythm: Signal Processing',
  'image':require('./../../content/projects/balancingRobot/1.png'),
  'text':'By determining the amount of energy in the sound signal and scaling it, the beat of the song can be determined by sampling (the energy of the sound signal on the beat is higher than between beats). This is shown in the image below (the peaks are on the beat). The LED flash was then used to make sure that the program is detecting the beat correctly.'
}

function BalancingRobot(props) {
    return(
      <div>
        <ProjectHeader
         projectKey='balancingRobot'
        />
        <HeroImage
         heroImage={data.heroImage}
        />
        <SectionOne
         title={a.title}
         text={a.text}
         image={a.image}
        />
        <div className='player-wrapper'>
          <ReactPlayer
           className='react-player'
           url={data.video1}
           playing
           volume={10}
           muted
           loop
           height='400px'
           width='30%'
          />
        </div>

      </div>
    )
}

export default BalancingRobot;
