import React from 'react'; // Import the Component component from React

import ProjectHeader from './../projectPage/ProjectHeader.js'
import HeroImage from './../projectPage/HeroImage.js'
import SectionOne from './../projectPage/SectionOne.js'
import Image from './../projectPage/Image.js'


const data = {
  'heroImage':require('./../../content/projects/bigData/hero-image.png'),
}

const a = {
  'title':'Explaining',
  'text':'Linear regression was used to understand which factors correlate with knife crime. The R-squared value, tells us how correlated our variables are. The maximum R-squared value is 1 and the minimum is 0 and a value of greater than 0.64 is considered a strong correlation.',
  'image':require('./../../content/projects/bigData/1.png'),
  'image2':require('./../../content/projects/bigData/2.png')
}

const b = {
  'title':'Predicting',
  'text':'',
  'image':require('./../../content/projects/bigData/3.png'),
}



function BigData(props) {
    return(
      <div>
        <ProjectHeader
         projectKey='bigData'
        />
        <div className="line-separator"></div>
        <HeroImage
         heroImage={data.heroImage}
        />
        <SectionOne
         title={a.title}
         text={a.text}
         image={a.image}
        />
        <SectionOne
         title={b.title}
         text={b.text}
         image={b.image}
        />
      </div>
    )
}

export default BigData;
